import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Cards = ({ breed }) => {
const [dogs, setDogs] = useState([]);
const [page, setPage] = useState(1);
const [prevQuery, setPrevQuery] = useState(null);
  const [nextQuery, setNextQuery] = useState(null);


useEffect(() => {

    const fetchData = async () => {
      try {
            const response = await axios.get(
            `https://frontend-take-home-service.fetch.com/dogs/search`,
            {withCredentials:true,
            headers: {
              'fetch-api-key': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpYXQiOjE2NzgzMDU2MTF9.Ky49nXH6qgHJQ0CBsZGYsP7_Is2am3u5j3RAdEl457s',
            },
            params: {breeds: [breed],
            size: 10, 
            from: page > 1 ? prevQuery : null,},});

    const dogIds = response.data.resultIds;
    const next = response.data.next;
    const prev = response.data.prev;
    setNextQuery(next);
    setPrevQuery(prev);
    // console.log("next is " +next)
        const dogsResponse = await axios.post(
        `https://frontend-take-home-service.fetch.com/dogs`,
        dogIds,
        {withCredentials :true,
        headers: {'fetch-api-key': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpYXQiOjE2NzgzMDU2MTF9.Ky49nXH6qgHJQ0CBsZGYsP7_Is2am3u5j3RAdEl457s',},});    

    const dogsData = dogsResponse.data;
    setDogs(dogsData);
    } catch(error){
            console.error(error);
    }
    };
    fetchData();
    }, [breed,page,prevQuery]);
    const handlePrevClick = () => {
      if (prevQuery) {
        setPage((prevPage) => prevPage - 1);
      }
    };

    const handleNextClick = () => {
      if (nextQuery) {
        setPage((prevPage) => prevPage + 1);
      }
    };
  return (
    <div>
      {
      dogs.map((dog) => (
        <div key={dog.id}>
          <img src={dog.img} alt={dog.name} />
          <h3>{dog.name}</h3>
          <p>Breed: {dog.breed}</p>
          <p>Age: {dog.age}</p>
          <p>Zip Code: {dog.zip_code}</p>

        </div>

      ))

      }
      <div>
        <button>Prev</button>
        <p>{page}</p>
        <button>Next</button>
      </div>
    </div>


  );
};

export default Cards;
