import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Match from './Match';


const Cards = ({ breed }) => {
  const [dogs, setDogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [favorites, setFavorites] = useState([]);
  const [match, setMatch] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `https://frontend-take-home-service.fetch.com/dogs/search`,
          {
            withCredentials:true,
            headers: {
              'fetch-api-key':'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpYXQiOjE2NzgzMDU2MTF9.Ky49nXH6qgHJQ0CBsZGYsP7_Is2am3u5j3RAdEl457s',
            },
            params: {
              breeds: [breed],
              sort: 'breed:asc',
              from: (currentPage - 1) * 25,
              size: 25,
            }
          }
        );

        const dogIds = response.data.resultIds;
        const next = response.data.next;
        const prev = response.data.prev;
        console.log("next is " +next)
        const dogsResponse = await axios.post(
          `https://frontend-take-home-service.fetch.com/dogs`,
          dogIds,
          {
            withCredentials: true,
            headers: {
              'fetch-api-key':'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpYXQiOjE2NzgzMDU2MTF9.Ky49nXH6qgHJQ0CBsZGYsP7_Is2am3u5j3RAdEl457s',
            },
          }
        );

        const dogsData = dogsResponse.data;
        setDogs(dogsData);
        setTotalPages(Math.ceil(response.data.total / 25));
      } catch(error){
        console.error(error);
      }
    };

    fetchData();
  }, [breed, currentPage]);

  const handleMatch = async () => {
    try {
      const response = await axios.post(
        `https://frontend-take-home-service.fetch.com/dogs/match`,
        dogs.map((dog) => dog.id),
        {
          withCredentials: true,
          headers: {
            'fetch-api-key':'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpYXQiOjE2NzgzMDU2MTF9.Ky49nXH6qgHJQ0CBsZGYsP7_Is2am3u5j3RAdEl457s',
          },
        }
      );
      setMatch(response.data.match);
    } catch (error) {
      console.error(error);
    }
  };

  const handlePrevPage = () => {
    setCurrentPage(currentPage - 1);
  };

  const handleNextPage = () => {
    setCurrentPage(currentPage + 1);
  };

  const handleFavorite = (id) => {
    const dog = dogs.find((dog) => dog.id === id);
    if (favorites.includes(dog)) {
      setFavorites(favorites.filter((f) => f.id !== id));
    } else {
      setFavorites([...favorites, dog]);
    }
  };
  console.log(favorites)
  return (
    <div>
      {match&& <Match favourites={favorites}/>}
      {dogs.map((dog) => (
        <div key={dog.id}>
          <img src={dog.img} alt={dog.name} />
          <h3>{dog.name}</h3>
          <p>Breed: {dog.breed}</p>
          <p>Age: {dog.age}</p>
          <p>Zip Code: {dog.zip_code}</p>
          <button onClick={() => handleFavorite(dog.id)}>
            {favorites.includes(dog) ? 'Remove from Favorites' : 'Add to Favorites'}
          </button>
        </div>
      ))}
      <div>
        {currentPage > 1 && <button onClick={handlePrevPage}>Prev</button>}
        <p>{currentPage} of {totalPages}</p>
        {currentPage < totalPages && <button onClick={handleNextPage}>Next</button>}
        <button onClick={handleMatch}>Find a Match</button>
        {/* {favorites.length > 0 && <button onClick={() => setMatch(true)}>Find a Match</button>} */}

      </div>

    </div>
  );
};

export default Cards;
