import React, { useState, useEffect } from 'react';
import axios from 'axios';



const Cards = ({ breed }) => {
  const [dogs, setDogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [selectedBreed, setSelectedBreed] = useState(breed);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `https://frontend-take-home-service.fetch.com/dogs/search`,
          {
            withCredentials: true,
            headers: {
              'fetch-api-key': 'your-api-key',
            },
            params: {
              breeds: [selectedBreed],
              from: (currentPage - 1) * 25,
              size: 25,
              sort: 'name:asc'
            }
          }
        );

        const dogIds = response.data.resultIds;
        const next = response.data.next;
        const prev = response.data.prev;

        const dogsResponse = await axios.post(
          `https://frontend-take-home-service.fetch.com/dogs`,
          dogIds,
          {
            withCredentials: true,
            headers: {
              'fetch-api-key': 'your-api-key',
            },
          }
        );

        const dogsData = dogsResponse.data;
        setDogs(dogsData);
        setTotalPages(Math.ceil(response.data.total / 25));
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, [selectedBreed, currentPage]);

  const handlePrevPage = () => {
    setCurrentPage(currentPage - 1);
  };

  const handleNextPage = () => {
    setCurrentPage(currentPage + 1);
  };

  const handleBreedChange = (event) => {
    setSelectedBreed(event.target.value);
    setCurrentPage(1);
  };

  return (
    <div>
      <div>
        <label htmlFor="breed-filter">Filter by breed:</label>
        <select
          id="breed-filter"
          name="breed-filter"
          value={selectedBreed}
          onChange={handleBreedChange}
        >
          {breed.map((breedOption, index) => (
            <option key={index} value={breedOption}>
              {breedOption}
            </option>
          ))}
        </select>
      </div>
      {dogs.map((dog) => (
        <div key={dog.id}>
          <img src={dog.img} alt={dog.name} />
          <h3>{dog.name}</h3>
          <p>Breed: {dog.breed}</p>
          <p>Age: {dog.age}</p>
          <p>Zip Code: {dog.zip_code}</p>
        </div>
      ))}
      <div>
        {currentPage > 1 && <button onClick={handlePrevPage}>Prev</button>}
        <p>
          {currentPage} of {totalPages}
        </p>
        {currentPage < totalPages && (
          <button onClick={handleNextPage}>Next</button>
        )}
      </div>
    </div>
  );
};

export default Cards;


