import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Cards = ({ breed }) => {
  const [dogs, setDogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    const fetchData = async () => {
        setLoading(true);
      try {
        const response = await axios.get(
          `https://frontend-take-home-service.fetch.com/dogs/search`,
          {withCredentials:true,
            headers: {
              'fetch-api-key': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpYXQiOjE2NzgzMDU2MTF9.Ky49nXH6qgHJQ0CBsZGYsP7_Is2am3u5j3RAdEl457s',
            },
            params: {
              breeds: [breed],
            },
          }
        );
        const dogIds = response.data.resultIds;
        const dogsResponse = await axios.post(
          `https://frontend-take-home-service.fetch.com/dogs?breeds[]=${breed}&size=10&page=${page}`,
          dogIds,
          {withCredentials :true,
            headers: {
              'fetch-api-key': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpYXQiOjE2NzgzMDU2MTF9.Ky49nXH6qgHJQ0CBsZGYsP7_Is2am3u5j3RAdEl457s',
            },
          }
        );
        const dogsData = dogsResponse.data;
        setDogs(dogsData);
        setTotalPages(Math.ceil(response.data.total / 6));
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, [breed,page]);
  const handlePageChange = (newPage) => {
    setPage(newPage);
  };

  return (
    <div>
      {dogs.map((dog) => (
        <div key={dog.id}>
          <img src={dog.img} alt={dog.name} />
          <h3>{dog.name}</h3>
          <p>Breed: {dog.breed}</p>
          <p>Age: {dog.age}</p>
          <p>Zip Code: {dog.zip_code}</p>
        </div>
      ))}

{totalPages > 1 && (
                <div>
                  {page > 1 && (
                    <button onClick={() => handlePageChange(page - 1)}>Prev</button>
                  )}
                  {page < totalPages && (
                    <button onClick={() => handlePageChange(page + 1)}>Next</button>
                  )}
                </div>
              )}
    </div>
  );
};

export default Cards;



