import React, { useState, useEffect } from 'react';
import axios from 'axios';




const Cards = ({ breed }) => {
  const [dogs, setDogs] = useState([]);
  const [total, setTotal] = useState(0);
  const [prevUrl, setPrevUrl] = useState(null);
  const [nextUrl, setNextUrl] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [perPage, setPerPage] = useState(25);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          'https://frontend-take-home-service.fetch.com/dogs/search',
          {
            withCredentials: true,
            headers: {
              'fetch-api-key':
                'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpYXQiOjE2NzgzMDU2MTF9.Ky49nXH6qgHJQ0CBsZGYsP7_Is2am3u5j3RAdEl457s',
            },
            params: {
              breeds: [breed],
              size: perPage,
              from: prevUrl || nextUrl,
            },
          }
        );

        const dogIds = response.data.resultIds;
        const next = response.data.next;
        const prev = response.data.prev;
        setNextUrl(next);
        setPrevUrl(prev);
        setTotal(response.data.total);

        const dogsResponse = await axios.post(
          'https://frontend-take-home-service.fetch.com/dogs',
          dogIds,
          {
            withCredentials: true,
            headers: {
              'fetch-api-key':
                'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpYXQiOjE2NzgzMDU2MTF9.Ky49nXH6qgHJQ0CBsZGYsP7_Is2am3u5j3RAdEl457s',
            },
          }
        );

        const dogsData = dogsResponse.data;
        setDogs(dogsData);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, [breed, perPage, prevUrl, nextUrl]);

  const handlePrevPage = () => {
    setCurrentPage((currentPage) => currentPage - 1);
  };

  const handleNextPage = () => {
    setCurrentPage((currentPage) => currentPage + 1);
  };

  const totalPages = Math.ceil(total / perPage);

  return (
    <div>
      {dogs.map((dog) => (
        <div key={dog.id}>
          <img src={dog.img} alt={dog.name} />
          <h3>{dog.name}</h3>
          <p>Breed: {dog.breed}</p>
          <p>Age: {dog.age}</p>
          <p>Zip Code: {dog.zip_code}</p>
        </div>
      ))}
      <div>
        <button disabled={!prevUrl} onClick={handlePrevPage}>
          Prev
        </button>
        <p>
          Page {currentPage} of {totalPages}
        </p>
        <button disabled={!nextUrl} onClick={handleNextPage}>
          Next
        </button>
      </div>
    </div>
  );
};

export default Cards;


