import React, { useState } from 'react';
import axios from 'axios';

const Match = ({ favorites }) => {
  const [match, setMatch] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleMatch = async () => {
    setIsLoading(true);
    try {
      const response = await axios.post(
        `https://frontend-take-home-service.fetch.com/dogs/match`,
        favorites.map((dog) => dog.id),
        {
          withCredentials: true,
          headers: {
            'fetch-api-key': 'your-api-key',
          },
        }
      );
      const matchedDogId = response.data.match;
      const matchedDogResponse = await axios.get(
        `https://frontend-take-home-service.fetch.com/dogs/${matchedDogId}`,
        {
          withCredentials: true,
          headers: {
            'fetch-api-key': 'your-api-key',
          },
        }
      );
      const matchedDogData = matchedDogResponse.data;
      setMatch(matchedDogData);
    } catch (error) {
      console.error(error);
    }
    setIsLoading(false);
  };

  return (
    <div>
      <button onClick={handleMatch} disabled={isLoading}>
        {isLoading ? 'Finding a Match...' : 'Find a Match'}
      </button>
      {match && (
        <div>
          <p>You've been matched with:</p>
          <img src={match.img} alt={match.name} />
          <h3>{match.name}</h3>
          <p>Breed: {match.breed}</p>
          <p>Age: {match.age}</p>
          <p>Zip Code: {match.zip_code}</p>
        </div>
      )}
    </div>
  );
};

export default Match;
