import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Cards from './Cards';



const Search = () => {
  const [breed, setBreed] = useState("");
  const [breeds, setBreeds] = useState([]);
  const [dogs, setDogs] = useState([]);
  const [msg, setMsg] = useState("");

  const api_key = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpYXQiOjE2NzgzMDU2MTF9.Ky49nXH6qgHJQ0CBsZGYsP7_Is2am3u5j3RAdEl457s';

  useEffect(() => {
    const fetchBreeds = async () => {
      try {
        const response = await axios.get(
          'https://frontend-take-home-service.fetch.com/dogs/breeds',
          { withCredentials: true, headers: { 'fetch-api-key': api_key } }
        )
        setBreeds(response.data);
      }
      catch (error) {
        console.log(error);
      }
    }
    fetchBreeds();
  }, [])

  useEffect(() => {
    const fetchDogs = async () => {
      try {
        const response = await axios.get(
          `https://frontend-take-home-service.fetch.com/dogs/search?breeds=${breed}`,
          { withCredentials: true, headers: { 'fetch-api-key': api_key } }
        )
        setDogs(response.data.resultIds);
      }
      catch (error) {
        console.log(error);
      }
    }
    fetchDogs();
  }, [breed])

  const handleChange = (e) => {
    setMsg(e.target.value)
  }

  const handleClick = (event) => {
    event.preventDefault()
    setBreed(msg)
  }

  return (
    <div>
      <form className="search-bar">
        <input type="text" onChange={handleChange} value={msg} />
        <input type="submit" onClick={handleClick} />
      </form>
      <Cards dogs={dogs} />
    </div>
  );
}

export default Search;


